package org.wiky.letscorp.view;/** * Created by wiky on 7/3/16. */import android.content.Context;import android.util.AttributeSet;import android.widget.ScrollView;import me.everything.android.ui.overscroll.OverScrollDecoratorHelper;public class OverScrollView extends ScrollView {    public OverScrollView(Context context) {        super(context);        initialize(context);    }    public OverScrollView(Context context, AttributeSet attrs) {        super(context, attrs);        initialize(context);    }    public OverScrollView(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        initialize(context);    }    private void initialize(Context context) {        OverScrollDecoratorHelper.setUpOverScroll(this);    }}